mapper.select:
SELECT CONCAT(ROUND(start_location_x, 1), ',', ROUND(start_location_y, 1), '#', ROUND(end_location_x, 1), ',', ROUND(end_location_y, 1)) as location, COUNT(*) as count FROM riding_record_main WHERE (ROUND(start_location_x, 1) != ROUND(end_location_x, 1)) AND (ROUND(start_location_y, 1) != ROUND(end_location_y, 1))
AND DATE_FORMAT(start_time, '%Y-%m') = #{month}
GROUP BY CONCAT(ROUND(start_location_x, 1), ',', ROUND(start_location_y, 1), '#', ROUND(end_location_x, 1), ',', ROUND(end_location_y, 1)) ORDER BY count DESC

SELECT CONCAT(DATE_FORMAT(start_time, '%H:'), LPAD(FLOOR(MINUTE(start_time) / 10) * 10, 2, '0'), '~', DATE_FORMAT(start_time, '%H:'), LPAD(FLOOR(MINUTE(start_time) / 10) * 10 + 10, 2, '0')) AS period, COUNT(*) as count FROM riding_record_main
WHERE DATE_FORMAT(start_time, '%Y-%m') = #{month}
GROUP BY period ORDER BY count DESC
